import * as React from 'react';
import { StyleSheet, Text, View , TextInput, TouchableOpacity } from 'react-native';
import { Header } from 'react-native-elements';
import db from './localDB';

export default class App extends React.Component {
  constructor(){
    super();
    this.state={
      textType: "",

    chunks: [],
    }

  }
  render(){
    return (
       <View style={styles.container}>
          <Header backgroundColor={'green'}  
          centerComponent={{text:"Monkey_chunky", style: {color:"yellow",fontSize: 25}}}/>
          <View style={styles.appC}>
           <TextInput style={styles.text} onChangeText={(text)=>{this.setState({textType: text})}}
           value={this.state.textType}></TextInput>
           <TouchableOpacity style={styles.button} onPress={()=>{
            this.setState({chunks: db[this.state.textType].chunks})
           }}>
            <Text style={styles.textStyle}> Go </Text>
            </TouchableOpacity>
            <View> {this.state.chunks.map(item=>{
              return(
                <TouchableOpacity styles={styles.button} onPress={()=>{
                  this.setState
                }}>
                  <Text>{item}</Text>
                </TouchableOpacity>
              )})}</View>
            </View>

      </View>
    );
  }

}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    //justifyContent: 'center',
  },
  appC: {
    flex: 1,
    width: "100%",

    backgroundColor: 'yellow',
  },
  text:{
    borderWidth: 4,
    width:"80%",
    marginTop: 200,
    marginLeft: 40,
    alignItems: "center",
    justifyContent: "center",
  },
  button:{
    //borderWidth: 5,
    marginTop: 80,
    marginLeft: 20,
    borderRadius: 100,
    width:100,
    height:100,
    backgroundColor: "red",
    alignSelf: "center",
    alignItems: "center",
    justifyContent: "center",

  },
  textStyle: {
    fontSize: 30,
    fontWeight: 'bold',
  }

});
